import './App.css';
import Projects from './pages/Projects';
import {Header} from './components/Header';
import { useState } from 'react';

function App() {
  const[displayState, setDisplayState] = useState('')
  const displayPage = () => {
    if (displayState === ''){
      return (
       < Home/>
      )
    }
    if (displayState === 'projects'){
      return(
        <Projects/>
      )
    }
  }

  return (
    <div className="App">
      {displayPage()}

    </div>
  );

}

export default App;
