import React from 'react'

const Home = () => {


  // About me section


      return (
          <div className="about-section" id="about">
              <h2>About Me</h2>
              <p>My name is Thomas Carlson, and I am currently a student at the Unviersity of Kansas bootcamp for full stack web development.</p>
              <p>Before taking this bootcamp, I have been working in the field of both insurance, as well as the financial services industry.</p>
              <p>As a result of finishing this bootcamp, I have gained the experience of working with various web development applications. </p>
              <p>These applications include HTML, CSS, JavaScript, jQuery, Bootstrap, Node.js, Express.js, MySQL, MongoDB, and React.</p>
              <p>After finishing the bootcamp, I hope to further my skills and find a great opportunitiy within the web development field.</p>
              <img src="https://img.icons8.com/external-flaticons-lineal-color-flat-icons/64/null/external-html-computer-programming-flaticons-lineal-color-flat-icons.png" alt="HTML icon" className="icon"/>
              <img src="https://img.icons8.com/color/48/null/css3.png" alt="CSS icon" className="icon"/>
              <img src="https://img.icons8.com/color/48/null/mongodb.png" alt="MongoDB icon" className="icon"/>
          </div>
      );
  }




export default Home
